<template>
  <div class="register">
    <div class="insert-img">
      <div class="user-register">
        <h3 class="title">用户注册</h3>
        <div class="submit-area">
          <!--          用户昵称-->
          <div class="outer-box">
            <label for="username" class="user-names">
              <input type="text"
                     name="username"
                     id="username"
                     class="username"
                     v-model="username"
                     autocomplete="off"
                     @change="checkVal($event)"/>
              <span class="user-name">用户名</span>
            </label>
            <span class="hint" :class="{'error':nameIsError}">{{nameHint}}</span>
          </div>
          <!--          用户密码-->
          <div class="outer-box">
            <label for="pass" class="user-names">
              <input type="password"
                     name="pass"
                     id="pass"
                     class="pass"
                     v-model="pass"
                     @change="checkVal($event)"/>
              <span class="user-pass">密码</span>
            </label>
            <span class="hint" :class="{'error':passIsError}">{{passHint}}</span>
          </div>
          <!--          二次输入密码-->
          <div class="outer-box">
            <label for="passAgain" class="user-names">
              <input type="password"
                     name="passAgain"
                     id="passAgain"
                     class="pass-again"
                     v-model="passAgain"
                     @change="checkVal($event)"/>
              <span class="user-pass-again">再次输入密码</span>
            </label>
            <span class="hint" :class="{'error':passAgainIsError}">{{passAgainHint}}</span>
          </div>
          <!--          输入邮箱-->
          <div class="outer-box">
            <label for="email" class="user-names">
              <input type="password"
                     name="email"
                     id="email"
                     class="email"
                     v-model="email"
                     @change="checkVal($event)"/>
              <span class="user-email">邮箱输入密码</span>
            </label>
            <span class="hint" :class="{'error':emailIsError}">{{emailHint}}</span>
          </div>
          <div class="button-area">
            <input type="button" value="注册" name="register" class="submit-button"/>
            <router-link to="/operation/login" class="login-link">已有账号？去登录交流吧</router-link>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: "register",
  data() {
    return {
      username: "",
      nameHint: "用户名应该是以字母开头的字母数组组合",
      nameIsError: false,
      pass: "",
      passHint: "密码应该是6-16位字母数字组合",
      passIsError: false,
      passAgain: "",
      passAgainHint: "请保证与上次密码输入一致",
      passAgainIsError: false,
      email: "",
      emailHint: "邮箱格式:XXX@XX.com",
      emailIsError: false,
    }
  },
  methods: {
    checkVal(e) {
      if (this.username !== "") {
        e.target.nextSibling.classList.add("input-not-null");
      } else if (this.username !== "" && e.target.nextSibling.classList.contains("input-not-null")) {
        e.target.nextSibling.classList.remove("input-not-null");
      }
      if (this.pass !== "") {
        e.target.nextSibling.classList.add("input-not-null");
      } else if (this.pass !== "" && e.target.nextSibling.classList.contains("input-not-null")) {
        e.target.nextSibling.classList.remove("input-not-null");
      }
      if (this.passAgain !== "") {
        e.target.nextSibling.classList.add("input-not-null");
      } else if (this.passAgain !== "" && e.target.nextSibling.classList.contains("input-not-null")) {
        e.target.nextSibling.classList.remove("input-not-null");
      }
      if (this.email !== "") {
        e.target.nextSibling.classList.add("input-not-null");
      } else if (this.email !== "" && e.target.nextSibling.classList.contains("input-not-null")) {
        e.target.nextSibling.classList.remove("input-not-null");
      }
    }
  }
}
</script>

