            }

            .username, .pass, .pass-again, .email {
              height: 20px;
              border: none;
              border: 1px solid #ccc;
              border-radius: 5px;
            }

            .username {

              &:focus + .user-name {
                color: $focusColor;
                transform: translateX(-100%);
              }
            }

            .pass {
              &:focus + .user-pass {
                color: $focusColor;
                transform: translateX(-100%);
              }
            }

            .pass-again {
              &:focus + .user-pass-again {
                color: $focusColor;
                transform: translateX(-100%);
              }
            }

            .email {
              &:focus + .user-email {
                color: $focusColor;
                transform: translateX(-100%);
              }
            }
          }
        }

        .button-area {
          position: relative;

          .submit-button {
            border: none;
            border: 1px solid #ccc;
